    private void limpar() {
        limpaCampos(jPDadosPessoais);
        limpaCampos(jPEndereco);
        jSPResultado.set;
        limpaCampos(jPSalario);
        limpaCampos(jPDescontos);
        limpaCampos(jPEmpregadoHorista);
        limpaCampos(jPEmpregadoMensalista);
    }
